CREATE TABLE Record (
    RecordID INTEGER PRIMARY KEY,  -- auto-incrementing record ID
    CourseID INT NOT NULL,
    Speed INT NOT NULL,
    Time TIME(3) NOT NULL,
    PlayerID INT NOT NULL,
    Date DATE NOT NULL,
    DriverID INT NOT NULL,
    VehicleBodyID INT NOT NULL,
    VehicleTireID INT NOT NULL,
    VehicleGliderID INT NOT NULL,
    -- Foreign key constraints
    CONSTRAINT FK_RaceResults_Course FOREIGN KEY (CourseID) REFERENCES Course(CourseID),
    CONSTRAINT FK_RaceResults_Player FOREIGN KEY (PlayerID) REFERENCES Player(PlayerID),
    CONSTRAINT FK_RaceResults_Driver FOREIGN KEY (DriverID) REFERENCES Driver(DriverID),
    CONSTRAINT FK_RaceResults_VehicleBody FOREIGN KEY (VehicleBodyID) REFERENCES VehicleBody(VehicleBodyID),
    CONSTRAINT FK_RaceResults_VehicleTire FOREIGN KEY (VehicleTireID) REFERENCES VehicleTire(VehicleTireID),
    CONSTRAINT FK_RaceResults_VehicleGlider FOREIGN KEY (VehicleGliderID) REFERENCES VehicleGlider(VehicleGliderID)
);

